class Form extends Component {


    state = {
        showForm: true
    }

    handleSubmit = () => {
        this.setState({
            showForm: false
        })
    }

    render() {
        return (
            <div>
                <Button onClick={this.handleSubmit} variant="primary">Primary</Button>

                <form>
                    <label>
                        Name:
                <input type="text" name="name" />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}

export default Form;
