import React, { Component } from 'react';
import { Button } from 'react-bootstrap';

class Form extends Component {


    state = {
        showForm: false
    }

    handleSubmit = () => {
        this.setState({
            showForm: true,
            company: ''
        })
    }

    handleChange = (event) => {

        const { name, value } = event.target;

        this.setState({
            [name]: value
        }, () => console.log(`after state set => ${this.state.company}`))
    }

    form = () => {
        return (
            <div className='text-center'>
               <form>
                <label>
                    Company:
                    <input type="text" name="company" onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
            </form> 
            </div>
        )
    }

    render() {
        return (
            <div>
                <div className='text-center'>
                    <Button onClick={this.handleSubmit} variant="primary" className='btn-add-bill'>Add Bill</Button>
                </div>

                {
                    this.state.showForm &&
                    this.form()
                }

            </div>
        )
    }
}

export default Form;
